/* eslint-disable react/sort-comp */
/* eslint-disable class-methods-use-this */
/* eslint-disable react/no-unused-class-component-methods */
/* eslint-disable react/no-unused-state */
import AsyncStorage from '@react-native-async-storage/async-storage';
import React, { Component } from 'react';
import { Text, View, Image, StyleSheet, FlatList } from 'react-native';
import { ScrollView } from 'react-native-web';

class ViewPost extends Component {
  constructor(props) {
    super(props);

    this.state = {
      listData: '',
      first_Name: '',
      last_name: '',
      post_Id: this.props.route.params.postID,
    };
  }

  componentDidMount() {
    this.getPost();
  }

  getPost = async () => {
    const value = await AsyncStorage.getItem('@session_token');
    const id = await AsyncStorage.getItem('@user_id');
    const postId = this.state.post_Id;

    return fetch(
      'http://localhost:3333/api/1.0.0/user/' + id + '/post/' + postId,
      {
        headers: {
          'X-Authorization': value,
        },
      }
    )
      .then((response) => {
        if (response.status === 200) {
          this.setState({ post_Id: postId });
          return response.json();
        } else if (response.status === 401) {
          this.props.navigation.navigate('Login');
        } else if (response.status === 403) {
          this.setState({
            errorMsg: 'You can only view the posts of your friends!',
          });
        } else if (response.status === 404) {
          this.setState({ errorMsg: 'Posts not found?!' });
        } else if (response.status === 500) {
          this.setState({
            errorMsg: 'Server Error! Please relaod or try again later!',
          });
        } else {
          throw 'Something went wrong';
        }
      })
      .then((responseJson) => {
        this.setState({
          isLoading: false,

          text: responseJson.text,
          first_Name: responseJson.author.first_name,
          last_name: responseJson.author.last_name,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  };

  render() {
    return (
      <View style={styles.centeredView}>
        <ScrollView style={styles.backgrd}>
          <View style={styles.postContainer}>
            <Image
              style={styles.profileImgCont}
              source={require('../assets/nopic.png')}
            />
            <Text style={styles.nameTxt}>
              {this.state.first_Name} {this.state.last_name}
            </Text>
            <Text style={styles.contentSection}> {this.state.text} </Text>
          </View>
        </ScrollView>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  profileImg: {
    width: 100,
    height: 100,
    borderRadius: 80,
    borderColor: 'black',
    borderWidth: 1,
    justifyContent: 'center',
    alignItems: 'center',
    margin: 20,
  },
  centeredView: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#3b5998',
  },
  txt: {
    fontWeight: 'bold',
    color: 'black',
    margin: 2,
  },
  txtName: {
    fontWeight: 'bold',
    color: 'black',
    fontSize: 20,
  },
  editbtn: {
    alignItems: 'center',
    backgroundColor: '#8b9dc3',
    borderRadius: 10,
    padding: 10,
    margin: 5,
    borderWidth: 2,
  },
  backgrd: {
    backgroundColor: '#3b5998',
  },
  buttonContainer: {
    flexDirection: 'row',
  },
  postContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'white',
    borderRadius: 10,
    height: 300,
    width: 400,
    marginTop: 30,
    borderWidth: 2,
  },
  rightButtons: {
    flexDirection: 'row',
    marginLeft: 300,
  },
  profileImgCont: {
    resizeMode: 'contain',
    alignSelf: 'center',
    height: 30,
    width: 30,
  },
  position: {
    bottom: 200,
  },
  nameTxt: {
    fontWeight: 'bold',
    textAlign: 'center',
    textDecorationLine: 'underline',
  },
  button: {
    borderRadius: 20,
    padding: 10,
    elevation: 2,
    margin: 5,
    width: 170,
  },
  textStyle: {
    color: 'white',
    fontWeight: 'bold',
    textAlign: 'center',
  },
  contentSection: {
    marginTop: 10,
    marginBottom: 15,
  },
});

export default ViewPost;
