/* eslint-disable react/sort-comp */
/* eslint-disable class-methods-use-this */
/* eslint-disable react/no-unused-class-component-methods */
/* eslint-disable react/no-unused-state */
import AsyncStorage from '@react-native-async-storage/async-storage';
import React, { Component } from 'react';
import {
  Text,
  View,
  ToastAndroid,
  Alert,
  Image,
  StyleSheet,
  TouchableOpacity,
  FlatList,
} from 'react-native';
import { ScrollView } from 'react-native-web';
import Icon from 'react-native-vector-icons/FontAwesome';
import { Button } from 'react-native-elements';

class ViewPost extends Component {
  constructor(props) {
    super(props);

    this.state = {
      firstName: '',
      lastName: '',
      friendCount: '',
      photo: null,
      isLoading: true,
      userId: '',
      listData: '',
      friendsId: this.props.route.params.friendId,
    };
  }

  componentDidMount() {
    this.unsubscribe = this.props.navigation.addListener('focus', () => {
      this.getPost();
      this.getFriendData();
      this.get_profile_image();
    });
  }

  checkLoggedIn = async () => {
    const { navigation } = this.props;
    const value = await AsyncStorage.getItem('@session_token');
    if (value !== null) {
      this.setState({ token: value });
    } else {
      navigation.navigate('login');
    }
  };

  render() {
    const { firstName, lastName, friendCount, listData } = this.state;
    const { navigation } = this.props;
    return (
      <ScrollView style={styles.backgrd}>
        <Image
          source={{ uri: 'https://reactjs.org/logo-og.png' }}
          style={styles.profileImg}
        />

        <Text style={styles.txtName}>
          {firstName} {lastName}
        </Text>
        <Text style={styles.txt}>Friends: {friendCount}</Text>
        <FlatList
          data={listData}
          renderItem={({ item }) => (
            <View style={styles.postContainer}>
              <Image
                style={styles.profileImgCont}
                source={require('../assets/nopic.png')}
              />
              <Text style={styles.nameTxt}>
                {item.author.first_name} {item.author.last_name}
              </Text>
              <Text style={styles.contentSection}> {item.text} </Text>
              <View style={styles.rightButtons}>
                <Button
                  icon={<Icon name="eye" size={15} color="white" />}
                  onPress={() => {
                    navigation.navigate('viewPost');
                  }}
                />
              </View>
            </View>
          )}
        />
      </ScrollView>
    );
  }
}
const styles = StyleSheet.create({
  profileImg: {
    width: 100,
    height: 100,
    borderRadius: 80,
    borderColor: 'black',
    borderWidth: 1,
    justifyContent: 'center',
    alignItems: 'center',
    margin: 20,
  },
  txt: {
    fontWeight: 'bold',
    color: 'black',
    margin: 2,
  },
  txtName: {
    fontWeight: 'bold',
    color: 'black',
    fontSize: 20,
  },
  editbtn: {
    alignItems: 'center',
    backgroundColor: '#8b9dc3',
    borderRadius: 10,
    padding: 10,
    margin: 5,
    borderWidth: 2,
  },
  backgrd: {
    backgroundColor: '#3b5998',
  },
  buttonContainer: {
    flexDirection: 'row',
  },
  postContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'white',
    borderRadius: 10,
    height: 300,
    width: 400,
    marginTop: 30,
    borderWidth: 2,
  },
  rightButtons: {
    flexDirection: 'row',
    marginLeft: 300,
  },
  profileImgCont: {
    resizeMode: 'contain',
    alignSelf: 'center',
    height: 30,
    width: 30,
  },
  position: {
    bottom: 200,
  },
  nameTxt: {
    fontWeight: 'bold',
    textAlign: 'center',
    textDecorationLine: 'underline',
  },
  button: {
    borderRadius: 20,
    padding: 10,
    elevation: 2,
    margin: 5,
    width: 170,
  },
  textStyle: {
    color: 'white',
    fontWeight: 'bold',
    textAlign: 'center',
  },
  contentSection: {
    marginTop: 10,
    marginBottom: 15,
  },
});

export default ViewPost;
