import React, { Component } from 'react';
import {
  Text,
  View,
  StyleSheet,
  Pressable,
  Modal,
  TextInput,
  FlatList,
  Button,
  ScrollView,
} from 'react-native';

import AsyncStorage from '@react-native-async-storage/async-storage';

class HomeScreen extends Component {
  constructor(props) {
    super(props);

    this.state = {
      modalVisible: false,
      userInput: '',
      listData: '',
    };
  }

  componentDidMount() {
    this.unsubscribe = this.props.navigation.addListener('focus', () => {
      this.getPost();
    });
  }

  setModalVisible = (visible) => {
    this.setState({ modalVisible: visible });
  };

  sendPost = async () => {
    const { navigation } = this.props;
    const token = await AsyncStorage.getItem('@session_token');
    const userId = await AsyncStorage.getItem('@user_id');
    const post = { text: this.state.userInput };
    fetch(`http://localhost:3333/api/1.0.0/user/${userId}/post`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'X-Authorization': token,
      },

      body: JSON.stringify(post),
    }).then((response) => {
      if (response.status === 200) {
        return response.json();
      }
      if (response.status === 400) {
        console.log('Bad Request');
      } else if (response.status === 401) {
        navigation.navigate('login');
      } else {
        throw 'Something went wrong';
      }
    });
  };

  getPost = async () => {
    const { navigation } = this.props;
    const token = await AsyncStorage.getItem('@session_token');
    const userID = await AsyncStorage.getItem('@user_id');
    return fetch(`http://localhost:3333/api/1.0.0/user/${userID}/post`, {
      method: 'GET',
      headers: {
        'X-Authorization': token,
      },
    })
      .then((response) => {
        if (response.status === 200) {
          return response.json();
        }
        if (response.status === 400) {
          console.log('Bad Request');
        } else if (response.status === 401) {
          navigation.navigate('login');
        } else {
          throw 'Something went wrong';
        }
      })
      .then((responseJson) => {
        this.setState({
          listData: responseJson,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  };

  render() {
    if (this.state.isLoading) {
      return (
        <View>
          <Text>Loading...</Text>
        </View>
      );
    }

    console.log('here', this.state);
    return (
      <ScrollView contentContainerStyle={styles.scrollView}>
        <View>
          <Button
            style={styles.buttonStyle}
            title="Add post to my wall"
            onPress={() => this.props.navigation.navigate('New Post')}
          />

          <View style={styles.flexContainer}>
            <FlatList
              data={this.listData}
              renderItem={({ item }) => (
                <View style={styles.itemContainer}>
                  <Text>
                    {item.author.first_name} {item.author.last_name}:
                  </Text>
                  <Text>{item.text}</Text>
                  <Text>Likes: {item.numLikes}</Text>

                  <View style={styles.buttonContainer}>
                    <Button
                      style={styles.buttonStyle}
                      title="View"
                      onPress={() => {
                        this.setState({ to_be_edited: item }, () => {
                          this.viewPost();
                        });
                      }}
                    />

                    {item.author.user_id == this.state.login_info.id ? (
                      <Button
                        style={styles.buttonStyle}
                        title="Edit"
                        onPress={() => {
                          this.setState(
                            { to_be_edited: item, isLoading: true },
                            () => {
                              this.editPost();
                            }
                          );
                        }}
                      />
                    ) : null}

                    {item.author.user_id == this.state.login_info.id ? (
                      <Button
                        style={styles.buttonStyle}
                        title="Delete"
                        onPress={() => {
                          this.deletePost(item.post_id);
                          this.setState({ feed: {}, isLoading: true }, () => {
                            this.getFeed();
                          });
                        }}
                      />
                    ) : null}
                  </View>
                </View>
              )}
              keyExtractor={(item, index) => item.post_id}
            />
          </View>
        </View>
      </ScrollView>
    );
  }
}

const styles = StyleSheet.create({
  flexContainer: {
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
  },

  outerContainer: {
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  },

  itemContainer: {
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
    marginBottom: 20,
  },

  buttonStyle: {
    width: 50,
    height: 50,
    alignItems: 'center',
  },

  buttonContainer: {
    flex: 1,
    flexDirection: 'row',
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
  },

  headerStyle: {
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'space-around',
    alignItems: 'flex-start',
  },

  scrollView: {
    flex: 1,
    height: '100%',
    width: '100%',
    margin: 20,
    alignSelf: 'center',
  },
});

export default HomeScreen;
