import React, { Component } from 'react';
import {
  Text,
  ScrollView,
  StyleSheet,
  TouchableOpacity,
  FlatList,
  View,
  Button,
  TextInput,
  Image,
} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

class FriendsList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      listData: [],
      searchData: [],
      searchFriends: [],
      nextDisabled: true,
      backDisabled: true,
      limit: 5,
      offset: 0,
    };
  }

  componentDidMount() {
    const { navigation } = this.props;
    this.unsubscribe = navigation.addListener('focus', () => {
      this.getFriends();
    });
  }

  viewFriend = (friendId) => {
    const { navigation } = this.props;
    navigation.navigate('friendScreen', {
      friendId: friendId,
    });
  };

  getData = async (page) => {
    //- 1 go back
    //0 just search
    //1 next page
    if (page == -1) {
      let temp = this.state.offset - this.state.limit;
      this.setState({
        offset: temp,
        nextDisabled: false,
      });
      if (temp == 0) {
        this.setState({
          backDisabled: true,
        });
      }
    } else if (page == 0) {
      this.setState({
        offset: 0,
        nextDisabled: false,
      });
    } else if (page == 1) {
      let temp = this.state.offset + this.state.limit;
      this.setState({
        offset: temp,
        backDisabled: false,
      });
    }
    this.setState({ validationText: '' });
    let searchMethod = 'all';
    if (this.state.friendSearch) {
      searchMethod = 'friends';
    }
    if (this.state.userInput !== '') {
      const value = await AsyncStorage.getItem('@session_token');
      return fetch(
        `http://localhost:3333/api/1.0.0/search?q=${this.state.userInput}&search_in=${searchMethod}&limit=${this.state.limit}&offset=${this.state.offset}`,
        {
          headers: {
            'X-Authorization': value,
          },
        }
      )
        .then((response) => {
          if (response.status === 200) {
            this.setState({
              userInput: '',
            });
            return response.json();
          } else if (response.status === 400) {
            console.log('Bad Request');
          } else if (response.status === 401) {
            this.props.navigation.navigate('login');
          } else {
            throw 'Something went wrong';
          }
        })
        .then((responseJson) => {
          console.log(responseJson.length);
          if (responseJson.length < 5) {
            this.setState({
              listData: responseJson,
              limit: 5,
              nextDisabled: true,
            });
          } else {
            this.setState({
              listData: responseJson,
              limit: 5,
            });
          }
        })
        .catch((error) => {
          console.log(error);
        });
    } else {
      this.setState({
        validationText: 'You have not entered Text in the text box',
        backDisabled: true,
      });
    }
  };
  render() {
    const { navigation } = this.props;
    const { listData, searchData, searchFriends } = this.state;
    return (
      <ScrollView style={styles.view}>
        <TouchableOpacity
          style={styles.addButton}
          onPress={() => {
            navigation.navigate('add');
          }}
        >
          <Text style={{ color: 'white' }}>Add Friends</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.addButton}
          onPress={() => {
            navigation.navigate('request');
          }}
        >
          <Text style={{ color: 'white' }}>Friend Request</Text>
        </TouchableOpacity>
        <TextInput
          placeholder="Search..."
          onChangeText={(searchFriends) => this.setState({ searchFriends })}
          value={searchFriends}
        />
        <Button title="Search:" onPress={() => this.searchFriends()} />
        <FlatList
          data={searchData}
          renderItem={({ item }) => (
            <View style={{ flexDirection: 'row' }}>
              <Image source={require('../assets/friends.png')} />
              <Text>
                {item.user_givenname} {item.user_familyname}
              </Text>
            </View>
          )}
        />
        <Text> My Friends </Text>
        <FlatList
          data={listData}
          renderItem={({ item }) => (
            <View style={{ flexDirection: 'row' }}>
              <Text>
                {item.user_givenname} {item.user_familyname}
              </Text>
              <TouchableOpacity
                onPress={() => {
                  this.viewFriend(item.user_id);
                }}
              >
                <Text style={{ color: 'white' }}>View</Text>
              </TouchableOpacity>
            </View>
          )}
        />
      </ScrollView>
    );
  }
}

const styles = StyleSheet.create({
  view: {
    backgroundColor: '#3b5998',
  },
  addButton: {
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#8b9dc3',
    padding: 10,
    margin: 5,
    borderRadius: 10,
  },
});

export default FriendsList;
