import React, { Component } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import {
  ScrollView,
  Text,
  Button,
  TextInput,
  FlatList,
  View,
  StyleSheet,
} from 'react-native-web';

class AddFriend extends Component {
  constructor(props) {
    super(props);

    this.state = {
      userInput: '',
      listData: [],
    };
  }

  componentDidMount() {
    const { navigation } = this.props;
    this.unsubscribe = navigation.addListener('focus', () => {
      this.checkLoggedIn();
    });
  }

  componentWillUnmount() {
    this.unsubscribe();
  }

  checkLoggedIn = async () => {
    const { navigation } = this.props;
    const token = await AsyncStorage.getItem('@session_token');
    if (token == null) {
      navigation.navigate('login');
    }
  };

  getData = async () => {
    // Validation (Cant search for yourself or friends you've already added)
    const { userInput } = this.state;
    const { navigation } = this.props;
    const token = await AsyncStorage.getItem('@session_token');
    return fetch(`http://localhost:3333/api/1.0.0/search?q=${userInput}`, {
      method: 'GET',
      headers: {
        'X-Authorization': token,
      },
    })
      .then((response) => {
        if (response.status === 200) {
          return response.json();
        }
        if (response.status === 401) {
          navigation.navigate('login');
        } else if (response.status === 404) {
          throw new Error('Not found');
        } else {
          throw 'Something went wrong';
        }
      })
      .then((responseJson) => {
        this.setState({
          listData: responseJson,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  };

  addRequest = async (userId) => {
    const token = await AsyncStorage.getItem('@session_token');
    const { navigation } = this.props;
    fetch(`http://localhost:3333/api/1.0.0/user/${userId}/friends`, {
      method: 'POST',
      headers: {
        'X-Authorization': token,
      },
    }).then((response) => {
      if (response.status === 200) {
        return response.json();
      }
      if (response.status === 401) {
        navigation.navigate('login');
      } else if (response.status === 403) {
        throw new Error('User is already added as a friend');
      } else if (response.status === 404) {
        throw new Error('Not found');
      } else {
        throw 'Something went wrong';
      }
    });
  };

  render() {
    const { userInput, listData } = this.state;
    return (
      <View>
        <ScrollView style={styles.backgrd}>
          <TextInput
            placeholder="Search..."
            onChangeText={(userInput) => this.setState({ userInput })}
            value={userInput}
          />
          <Button title="Search:" onPress={() => this.getData()} />

          <FlatList
            data={listData}
            renderItem={({ item }) => (
              <View style={{ flexDirection: 'row' }}>
                <Text>
                  {item.user_givenname} {item.user_familyname}
                  <Button
                    title="Add"
                    onPress={() => this.addRequest(item.user_id)}
                  />
                </Text>
              </View>
            )}
          />
        </ScrollView>
      </View>
    );
  }
}
const styles = StyleSheet.create({
  backgrd: {
    backgroundColor: '#3b5998',
  },
});

export default AddFriend;
