import React, { Component } from 'react';
import { Text, View } from 'react-native';

class ProfileScreen extends Component {
  // Show friend request (Accept/Reject this)

  // Show current friends
  render() {
    if (this.state.isLoading) {
      return (
        <View
          style={{
            flex: 1,
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <Text>Loading</Text>
        </View>
      );
    } else {
      return (
        <ScrollView>
          <FlatList
            data={this.state.friendRequestData}
            renderItem={({ item, index }) => (
              <View>
                <Text>
                  {item.first_name} {item.last_name}
                </Text>
                <Button
                  onPress={() => this.acceptFriendRequest()}
                  title="Accept"
                />
                <Button
                  onPress={() => this.declineFriendReques()}
                  title="Decline"
                />
              </View>
            )}
          />

          <Button
            title="Go Back"
            onPress={() => this.props.navigation.goBack()}
          />
          <Button title="Load Page" onPress={() => this.loadFriendRequests()} />
        </ScrollView>
      );
    }
  }
}

export default ProfileScreen;
