import React, { Component } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import {
  ScrollView,
  Text,
  Button,
  TextInput,
  FlatList,
  View,
  Switch,
  TouchableOpacity,
} from 'react-native-web';

class AddFriend extends Component {
  constructor(props) {
    super(props);

    this.state = {
      userInput: '',
      friendSearch: false,
      listData: [],
      selectedUserID: 0,
      validationText: '',
    };
  }
  //Having issues with the userInput but I can't properly think why
  //Look at this in depth tomorrow
  componentDidMount() {
    this.unsubscribe = this.props.navigation.addListener('focus', () => {
      this.checkLoggedIn();
    });
  }

  componentWillUnmount() {
    this.unsubscribe();
  }

  getData = async () => {};

  checkLoggedIn = async () => {
    const value = await AsyncStorage.getItem('@session_token');
    if (value == null) {
      this.props.navigation.navigate('login');
    }
  };

  render() {
    return (
      <View>
        <ScrollView>
          <TextInput
            placeholder="Enter name to search for"
            onChangeText={(userInput) => this.setState({ userInput })}
            value={this.state.userInput}
          />
          <Text>Friends</Text>
          <Button title="Search:" onPress={() => this.getData()} />
          <Text>{this.state.validationText}</Text>
          <FlatList
            data={this.state.listData}
            renderItem={({ item }) => (
              <View>
                <TouchableOpacity
                  onPress={() =>
                    this.props.navigation.navigate('userScreen', {
                      id: item.user_id,
                    })
                  }
                >
                  <Text>
                    {item.user_givenname} {item.user_familyname}
                  </Text>
                </TouchableOpacity>
              </View>
            )}
          />
        </ScrollView>
      </View>
    );
  }
}

export default AddFriend;
