import AsyncStorage from '@react-native-async-storage/async-storage';
import React, { Component, useState } from 'react';
import {
  Text,
  View,
  Image,
  StyleSheet,
  TouchableOpacity,
  FlatList,
  ScrollView,
  TextInput,
} from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';
import { Button } from 'react-native-elements';
const [FontSize, setFontSize] = useState(20);
const [text, setText] = useState('Make Text Bigger');
class ProfileScreen extends Component {
  constructor(props) {
    super(props);

    this.state = {
      firstName: '',
      lastName: '',
      photo: null,
      isLoading: true,
      listData: [],
      userInput: '',
    };
  }

  componentDidMount() {
    const { navigation } = this.props;
    this.unsubscribe = navigation.addListener('focus', () => {
      this.checkLoggedIn();
      this.getPost();
      this.getProfileData();
      this.get_profile_image();
    });
  }

  componentWillUnmount() {
    this.unsubscribe();
  }

  letChangeTextSize = () => {
    if (FontSize === 20) {
      setFontSize(32);
      setText('Make Text Smaller');
    } else {
      setFontSize(20);
      setText('Make Text Bigger');
    }
  };
  viewPost = (postId) => {
    const { navigation } = this.props;
    navigation.navigate('viewPost', {
      postID: postId,
    });
  };

  editPost = (postId) => {
    const { navigation } = this.props;
    navigation.navigate('editPost', {
      postID: postId,
    });
  };

  checkLoggedIn = async () => {
    const { navigation } = this.props;
    const token = await AsyncStorage.getItem('@session_token');
    if (token == null) {
      navigation.navigate('login');
    }
  };

  get_profile_image = async () => {
    const token = await AsyncStorage.getItem('@session_token');
    const userId = await AsyncStorage.getItem('@user_id');
    fetch(`http://localhost:3333/api/1.0.0/user/${userId}/photo`, {
      method: 'GET',
      headers: {
        'X-Authorization': token,
      },
    })
      .then((res) => res.blob())
      .then((resBlob) => {
        const data = URL.createObjectURL(resBlob);
        this.setState({
          photo: data,
          isLoading: false,
        });
      })
      .catch((err) => {
        console.log('error', err);
      });
  };

  sendPost = async () => {
    const { navigation } = this.props;
    const { userInput } = this.state;
    const token = await AsyncStorage.getItem('@session_token');
    const userId = await AsyncStorage.getItem('@user_id');
    const post = { text: userInput };

    if (userInput === '') {
      alert('You cannot send a blank post');
    } else if (userInput.length > 280) {
      alert('There is a 280 character limit');
    } else {
      fetch(`http://localhost:3333/api/1.0.0/user/${userId}/post`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': token,
        },
        body: JSON.stringify(post),
      }).then((response) => {
        if (response.status === 201) {
          this.getPost();
        }
        if (response.status === 401) {
          navigation.navigate('login');
        } else if (response.status === 404) {
          throw new Error('Not found');
        } else {
          throw 'Something went wrong';
        }
      });
    }
  };

  getPost = async () => {
    const { navigation } = this.props;
    const token = await AsyncStorage.getItem('@session_token');
    const userID = await AsyncStorage.getItem('@user_id');
    return fetch(`http://localhost:3333/api/1.0.0/user/${userID}/post`, {
      method: 'GET',
      headers: {
        'X-Authorization': token,
      },
    })
      .then((response) => {
        if (response.status === 200) {
          return response.json();
        }
        if (response.status === 401) {
          navigation.navigate('login');
        } else if (response.status === 403) {
          throw new Error('Can only view the post of yourself of your friends');
        }
        throw 'Something went wrong';
      })
      .then((responseJson) => {
        this.setState({
          isLoading: false,
          listData: responseJson,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  };

  deletePost = async (postId) => {
    const token = await AsyncStorage.getItem('@session_token');
    const userId = await AsyncStorage.getItem('@user_id');
    const { navigation } = this.props;
    fetch(`http://localhost:3333/api/1.0.0/user/${userId}/post/${postId}`, {
      method: 'DELETE',
      headers: {
        'X-Authorization': token,
      },
    }).then((response) => {
      if (response.status === 200) {
        this.getPost();
      }
      if (response.status === 401) {
        navigation.navigate('login');
      } else if (response.status === 403) {
        throw new Error('You can only delete your own post');
      }
      throw 'Something went wrong';
    });
  };

  getProfileData = async () => {
    const token = await AsyncStorage.getItem('@session_token');
    const userId = await AsyncStorage.getItem('@user_id');
    const { navigation } = this.props;
    return fetch(`http://localhost:3333/api/1.0.0/user/${userId}`, {
      method: 'GET',
      headers: {
        'X-Authorization': token,
        'Content-Type': 'application/json',
      },
    })
      .then((response) => {
        if (response.status === 200) {
          return response.json();
        }
        if (response.status === 401) {
          navigation.navigate('login');
        } else if (response.status === 404) {
          throw new Error('Not found');
        } else {
          throw 'Something went wrong';
        }
      })
      .then((responseJson) => {
        console.log(responseJson);
        this.setState({
          firstName: responseJson.first_name,
          lastName: responseJson.last_name,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  };

  render() {
    const { firstName, lastName, listData, userInput, photo, isLoading } =
      this.state;
    const { navigation } = this.props;
    if (isLoading) {
      return (
        <View>
          <Text> Loading... </Text>
        </View>
      );
    }
    return (
      <View style={styles.centeredView}>
        <ScrollView style={styles.backgrd}>
          <Image source={photo} style={styles.profileImg} />

          <Text style={styles.txtName}>
            {firstName} {lastName}
          </Text>

          <TouchableOpacity
            style={styles.editbtn}
            onPress={() => {
              navigation.navigate('edit'); // Nav to edit page
            }}
          >
            <Text style={{ color: 'white' }}>Upload Picture</Text>
          </TouchableOpacity>
          <TextInput
            placeholder="Whats on your mind..."
            onChangeText={(userInput) => this.setState({ userInput })}
            value={userInput}
            style={styles.input}
          />
          <TouchableOpacity
            style={styles.editbtn}
            onPress={() => {
              this.sendPost(); // Nav to edit page
            }}
          >
            <Text style={{ color: 'white' }}>Post</Text>
          </TouchableOpacity>
          <FlatList
            data={listData}
            renderItem={({ item }) => (
              <View style={styles.postContainer}>
                <Image style={styles.profileImgCont} source={photo} />
                <Text style={styles.nameTxt}>
                  {item.author.first_name} {item.author.last_name}
                </Text>
                <Text style={styles.contentSection}> {item.text} </Text>
                <Text> Likes: {item.numLikes} </Text>
                <View style={styles.rightButtons}>
                  <Button
                    icon={<Icon name="edit" size={15} color="white" />}
                    onPress={() => {
                      this.editPost(item.post_id);
                    }}
                  />
                  <Button
                    icon={<Icon name="eye" size={15} color="white" />}
                    onPress={() => {
                      this.viewPost(item.post_id);
                    }}
                  />
                  <Button
                    icon={<Icon name="trash" size={15} color="red" />}
                    onPress={() => {
                      this.deletePost(item.post_id);
                    }}
                  />
                </View>
              </View>
            )}
          />
        </ScrollView>
      </View>
    );
  }
